import React, { useEffect } from 'react';
import DataList from './DataList';
import API from '../utils/API';
import { useStoreContext } from '../utils/GlobalState';
import { ADD_SYMBOLS } from '../utils/actions';

const Search = () => {
    const [state, dispatch] = useStoreContext();

    const getSymbols = () => {
        // get all symbols for stocks
        // API.getSymbols()
        //     .then(results => {
        //     dispatch({
        //         type: ADD_SYMBOLS,
        //         symbols: results.data,
        //     });
        //     })
        //     .catch(err => console.log(err));
        dispatch({
            type: ADD_SYMBOLS,
            symbols: 'Hello',
        });
        };

    useEffect(() => {
       console.log('Triggered');
        getSymbols();
    }, []);

    return (
        <div>
            <input list="availableStocks" type="text" className="form-control" id="stockSearch" placeholder="Search Stock Symbols" />
        <DataList symbols={state.autoFillSymbols}/>
        </div>
    );
};

export default Search;
