import React, { useEffect } from 'react';
import DataList from './DataList';
import { useStoreContext } from '../utils/GlobalState';
import API from '../utils/API';
import {ADD_SYMBOLS} from '../utils/actions';


const Search = () => {
    const [state, dispatch] = useStoreContext();

    const getSymbols = () => {
        // get all symbols for stocks
        API.getSymbols()
            .then(results => {
            dispatch({
                type: ADD_SYMBOLS,
                symbols: results.data,
            });
            })
            .catch(err => console.log(err));
        };

    useEffect(() => {
        getSymbols();
    });

    return (
        <div>
            <input list="availableStocks" type="text" class="form-control" id="stockSearch" placeholder="Search Stock Symbols" />
            <datalist id="availableStocks">
            {
                state.autoFillSymbols.map(symbol =>  <option value={symbol.description} />)
            }
        </datalist>
        </div>
    );
};

export default Search;
